import { StatusBar, ImageBackground } from 'react-native';
import { FontAwesome } from '@expo/vector-icons';
import React from 'react';
import { useState } from 'react';
import { StyleSheet, Text, TextInput, TouchableOpacity, View ,  Button, Image,Platform } from 'react-native';
import theme from './src/styles/theme';
import * as ImagePicker from 'expo-image-picker';


export default function App() {
    const [image, setImage] = useState(null);

    const pickImage = async () => {
      // No permissions request is necessary for launching the image library
      let result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.All,
        allowsEditing: true,
        aspect: [4, 3],
        quality: 1,
      });

      console.log(result);

      if (!result.canceled) {
        setImage(result.assets[0].uri);
      }
    };


  return (

    <ImageBackground source={require('./src/assets/bk.jpeg')} style={[styles.background, { opacity: 0.9 }]}>

      <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <View style={styles.avatarContainer}>
  {image ? (
    <Image source={{ uri: image }} style={styles.avatarImage} />
  ) : (
    <>
      <FontAwesome name="upload" size={24} color="black" /> {/* √çcone de upload */}
      <Text style={styles.avatarText}>Select</Text>
    </>
  )}
</View>
        <Text style={styles.text}>Cadastre-se</Text>

        <TextInput style={styles.input}
          placeholder='Nome'
          // onChangeText={(txtIdade) => setIdade(parseInt(txtIdade))}
        />
        <TextInput style={styles.input}
          placeholder='E-mail'
          // onChangeText={(txtIdade) => setIdade(parseInt(txtIdade))}
        />

        <TextInput style={styles.input}
          placeholder='Senha'
          // onChangeText={(txtIdade) => setIdade(parseInt(txtIdade))}
        />
        <TextInput style={styles.input}
          placeholder='Confirmar Senha'
          // onChangeText={(txtIdade) => setIdade(parseInt(txtIdade))}
        />
        <TouchableOpacity style={styles.button}>
          <Text style={styles.buttonText}>Enviar</Text>
        </TouchableOpacity>
        <StatusBar />
      </View>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  background: {
    flex: 1,
    resizeMode: 'cover',
    justifyContent: 'center',
  },
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    paddingTop: 100,
  },
  text: {
    fontSize: 44,
    padding: 20,
    color: theme.colors.teste,
  },
  textInput: {
    borderColor: theme.colors.red,
    borderWidth: 1,
    borderRadius: 5,
    fontSize: 24,
    width: 300,
    paddingTop: 10,
    paddingBottom: 10
  },
  button: {
    backgroundColor: theme.colors.primaryLighter,
    padding: 10,
    borderRadius: 5,
    marginTop: 10,
  },
  buttonText: {
    color: theme.colors.white,
    fontSize: 16,
    fontWeight: 'bold',
  },
  input: {
    fontSize: 20,
    borderWidth: 2,
    borderRadius: 10,
    borderColor: '#264753',
    padding: 20,
    width: 300,
    marginBottom: 10,
    marginTop: 10,
    backgroundColor: theme.colors.white,
    color: theme.colors.black
  },
  avatarContainer: {
    width: 100,
    height: 100,
    borderRadius: 50,
    borderColor: '#264753',
    backgroundColor: 'lightgray',
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 10,
  },
  avatarImage: {
    width: 100,
    height: 100,
    borderRadius: 50, // half of width and height for a circle
  },
  avatarText: {
    fontSize: 20,
  },
});
