import { useNavigation } from '@react-navigation/native';
import React, { useState } from 'react';
import { Button, Image, View, Platform, Text, TextInput, StyleSheet} from 'react-native';
import { StackTypes } from '../../routes/stack';
import * as ImagePicker from 'expo-image-picker';
import UserService   from '../../services/userService';
import {User} from '../../types/types'
import { TouchableOpacity } from 'react-native-gesture-handler';
import CustomButton from '../../components/button';
import { InputLogin } from '../../components/InputLogin/style';
import { InputEmail } from '../../components/InputEmail/style';
import PassWordInput from '../../components/Password';
import PassWordInput2 from '../../components/ConfirmaPassword';


const Cadastro = () => {
  const [login, setEmail] = useState<string>('');
    const [image, setImage] = useState('');
    const [name, setName] =  useState<string>('');
    const [password, setPassword] = useState<string>('');
    const [password2, setPassword2] = useState<string>('')

    const pickImage = async () => {
      
      let result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.Images,
        allowsEditing: true,
        aspect: [1, 1],
        quality: 1,
      });
  
      console.log(result);
  
      if (!result.canceled) {
        setImage(result.assets[0].uri);
      }
    };

    const userService = new UserService();

    const handleUpload = async () => {
      try {
        const user: User = {
            username: name,
            password: password, // Defina a senha como necess√°rio
            photo: image
        };

        const { success, message } = await userService.addUser(user);
        if (success) {
          console.log(message);
          alert(message);
          navigation.navigate('Login');
        } else {
          alert(message);
         // console.log(message);
          navigation.navigate('Login');
        }
      } catch (error) {
        console.error('Error uploading image:', error);
      }
  };

    const navigation = useNavigation<StackTypes>();
    

return (
  
       
  <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
    <Text style={styles.title}>Cadastre-se</Text>
    {image && <Image style={styles.avatarContainer} source={{ uri: image }}  />}
  <InputLogin  
            placeholder="Login"
            onChangeText={setName}
            value={name}
          />
  <InputEmail  
            placeholder="E-mail"
            onChangeText={setEmail}
            value={login}
          />
  <PassWordInput 
          style={styles.text1}
          placeholder="Senha"
          onChangeText={setPassword}
          value={password}
        />
  <PassWordInput2
          style={styles.text1}
          placeholder="Confirmar senha"
          onChangeText={setPassword2}
          value={password2}
        />
  <CustomButton title='Selecionar Imagem' onPress={pickImage}></CustomButton>
  
  {/* {image && <Image source={{ uri: image }} style={{ width: 200, height: 200 , marginBottom: 10}} />} */}
  
  <CustomButton title='Confirmar' onPress={handleUpload}></CustomButton>
</View>
        
   

);

};
const styles = StyleSheet.create({
  text1:{
    fontSize: 16,
    padding: 5,
    color: '#E2001A', 
    fontFamily: 'Arial', 
    paddingLeft: 10
  },
  text: {
    fontSize: 16,
    padding: 5,
    color: '#E2001A', 
    fontWeight: 'bold', 
    fontFamily: 'Arial', 
    textAlign: 'left', 
    paddingRight:300
  },
  title:{
    fontSize: 50,
    fontWeight: 'bold',
    marginBottom: 8, 

    paddingHorizontal: 80,
    paddingBottom: 30,
    width: '100%', // Us
    color: '#E2001A'
  },
  avatarContainer: {
    width: 100,
    height: 100,
    borderRadius: 50, // half of width and height for a circle
    backgroundColor: 'lightgray',
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 20,
    borderWidth: 2,
    borderColor:'#E2001A' ,
    padding: 15,
  },
  avatarImage: {
    width: 100,
    height: 100,
    borderRadius: 50, // half of width and height for a circle
    
  },
});
export default Cadastro;
