import { StatusBar, ImageBackground } from 'react-native';
import React from 'react';
import { useState } from 'react';
import { StyleSheet, Text, TextInput, TouchableOpacity, View, Image, Platform } from 'react-native';
import theme from '../styles/theme'
import * as ImagePicker from 'expo-image-picker';
import { FontAwesome } from '@expo/vector-icons'; // Importe o ícone FontAwesome ou outro ícone da biblioteca
import { WebView } from 'react-native-webview';

export default function Convite() {

  return (

    //<ImageBackground source={require('./src/assets/back.png')} style={[styles.background]}>

      <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      
        <View style={{ alignItems: 'center' }}>
          <Text style={styles.text}>Voce foi convidado para o grupo X, pelo Y</Text>
        </View>

        <TouchableOpacity style={styles.button}>
          <Text style={styles.buttonText}>Entrar</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.button1}>
          <Text style={styles.buttonText}>Recusar</Text>
        </TouchableOpacity>
        <StatusBar />
      </View>
   // </ImageBackground>
  );
}

const styles = StyleSheet.create({
  background: {
    flex: 1,
    resizeMode: 'cover',
    justifyContent: 'center',
    //opacity: 0.9,
  },
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    paddingTop: 1,
  },
  text: {
    fontSize: 40,
    padding: 20,
    color: theme.colors.teste, // Altere para uma cor que contraste bem com o plano de fundo
    fontWeight: 'bold', // Deixa a fonte mais gorda
    fontFamily: 'Arial', // Altere para a fonte desejada
    letterSpacing: 2,
    marginTop:15,
    
  },
  textInput: {
    borderColor: theme.colors.teste,
    borderWidth: 1,
    borderRadius: 5,
    fontSize: 24,
    width: 300,
    paddingTop: 10,
    paddingBottom: 10
  },
  button: {
    backgroundColor: theme.colors.teste,
    padding: 10,
    borderRadius: 5,
    marginTop: 100,
  },
  button1: {
    backgroundColor: theme.colors.red,
    padding: 10,
    borderRadius: 5,
    marginTop: 25,
  },
  buttonText: {
    color: theme.colors.white,
    fontSize: 40,
    fontWeight: 'bold',
  },
  input: {
    fontSize: 17,
    borderWidth: 1,
    borderRadius:30,
    borderColor: theme.colors.teste,
    padding: 15,
    width: 300,
    marginBottom: 10,
    marginTop: 10,
    backgroundColor: theme.colors.white,
    color: theme.colors.black
  },

});
