import { useNavigation } from '@react-navigation/native';
import React, { useState } from 'react';
import { Button, Image, View, Platform, Text, TextInput } from 'react-native';
import { StackTypes } from './src/routes/stack';
import * as ImagePicker from 'expo-image-picker';
import UserService   from './src/services/userService';
import {User} from './src/services/types'

const Home = () => {
    const [image, setImage] = useState('');
    const [name, setName] = useState('');

    const pickImage = async () => {
      // No permissions request is necessary for launching the image library
      let result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.All,
        allowsEditing: true,
        aspect: [4, 3],
        quality: 1,
      });

      console.log(result);

      if (!result.canceled) {
        setImage(result.assets[0].uri);
      }
    };

    const userService = new UserService();

    const handleUpload = async () => {
      try {
        const user: User = {
            username: name,
            password: 'password', // Defina a senha como necessário
            photo: image
        };

          const userAdded = await userService.addUser(user);
          if (userAdded) {
              console.log('Usuário adicionado com sucesso!');
          } else {
              console.log('Erro ao adicionar usuário');
          }
      } catch (error) {
          console.error('Error uploading image:', error);
      }
  };

    const navigation = useNavigation<StackTypes>();

return (


  <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
  <Text>Nome:</Text>
  <TextInput
      style={{ height: 40, borderColor: 'gray', borderWidth: 1, marginBottom: 10 }}
      onChangeText={text => setName(text)}
      value={name}
  />
  <Button title="Pick an image from camera roll" onPress={pickImage} />
  {image && <Image source={{ uri: image }} style={{ width: 200, height: 200 }} />}
  <Button title="Upload" onPress={handleUpload} />
</View>



);

};

export default Home;
