import { useNavigation } from '@react-navigation/native';
import React, { useState } from 'react';
import { Button, Image, View, Platform, Text, TextInput, StyleSheet} from 'react-native';
import { StackTypes } from '../../routes/stack';
import * as ImagePicker from 'expo-image-picker';
import GroupService   from '../../services/groupService';
import {Group} from '../../types/group'
import { TouchableOpacity } from 'react-native-gesture-handler';
import CustomButton from '../../components/button';
import { InputLogin } from '../../components/InputLogin/style';
import PassWordInput from '../../components/Password';
import DateTimePicker from '@react-native-community/datetimepicker';

const CadastroGrupo = () => {
  
    const [image, setImage] = useState('');
    const [name, setName] = useState('');
    const [quantidade, setQuantidade] = useState('');
    const [valor, setValor] = useState('');
    const [descricao, setDescricao] = useState('');
    const [date, setDate] = useState(new Date());

    const pickImage = async () => {
      // No permissions request is necessary for launching the image library
      let result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.Images,
        allowsEditing: true,
        aspect: [4, 3],
        quality: 1,
      });
  
      console.log(result);
  
      if (!result.canceled) {
        setImage(result.assets[0].uri);
      }
    };

    const groupService = new GroupService();

    const handleUpload = async () => {
      try {
        const group: Group = {
            name: name,
            quantidade: 0,
            valor: 0,
            descricao: descricao ,
            data: '',
            photo: image
        };

          const groupAdded = await groupService.addGroup(group);
          if (groupAdded) {
              console.log('Grupo adicionado com sucesso!');
              alert('Grupo adicionado com sucesso!');
               navigation.navigate('Grupo');
          } else {
             // console.log('Erro ao adicionar grupo');
              alert('Grupo adicionado com sucesso!');
              navigation.navigate('Grupo');
          }
      } catch (error) {
          //console.error('Error uploading image:', error);
              //alert('Grupo adicionado com sucesso!');
              navigation.navigate('Grupo');
      }
      
  };

    const navigation = useNavigation<StackTypes>();

return (
  
       
  <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
  {image && <Image style={styles.avatarContainer} source={{ uri: image }}  />}
  
  <InputLogin  
            placeholder="Nome"
            onChangeText={setName}
            value={name}
          />
  
  <InputLogin  
            placeholder="Quantidade"
            onChangeText={setQuantidade}
            value={quantidade}
          />
          
  <InputLogin  
            placeholder="Valor"
            onChangeText={setValor}
            value={valor}
          />
          
  <InputLogin  
            placeholder="Descrição"
            onChangeText={setDescricao}
            value={descricao}
          />
        
            <DateTimePicker
                value={date}
                mode="date"
                display="default"
                onChange={(event, selectedDate) => {
                    const currentDate = selectedDate || date;
                    setDate(currentDate);
                }}
            />

  <CustomButton title='Selecionar Imagem' onPress={pickImage}></CustomButton>
  
  
  <CustomButton title='Salvar' onPress={handleUpload}></CustomButton>
</View>
        
   

);

};
const styles = StyleSheet.create({
 
  text: {
    fontSize: 18,
    padding: 5,
    color: '#E2001A', 
    fontWeight: 'bold', 
    fontFamily: 'Arial', 
    textAlign: 'left', 
  },
  avatarContainer: {
    width: 100,
    height: 100,
    borderRadius: 50, // half of width and height for a circle
    backgroundColor: 'lightgray',
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 25,
    borderWidth: 2,
    borderColor:'#E2001A' ,
    padding: 15,
  },
  avatarImage: {
    width: 100,
    height: 100,
    borderRadius: 50, // half of width and height for a circle
    
  },
});
export default CadastroGrupo;
